// modal.service.ts

import {
  ApplicationRef,
  ComponentRef,
  Inject,
  Injectable,
  Injector,
  Type,
  ViewContainerRef,
  inject,
} from "@angular/core";

import { APP_ROOT_VIEW_CONTAINER_REF } from "./app.tokens";

@Injectable({
  providedIn: "root",
  useExisting: APP_ROOT_VIEW_CONTAINER_REF,
})
export class ModalService {
  private modalRefs: { [key: string]: ComponentRef<any> } = {};
  private injector = inject(Injector);
  private viewContainerRef: ViewContainerRef | undefined = undefined;

  constructor() {}

  init(vcr: ViewContainerRef) {
    this.viewContainerRef = vcr;
  }

  open<T>(component: Type<T>): ComponentRef<T> {
    if (!this.viewContainerRef) {
      throw "ViewContainerRef is not initialized";
    }

    const componentRef = this.viewContainerRef?.createComponent(component, {
      injector: this.injector,
    });

    this.modalRefs[crypto.randomUUID()] = componentRef;

    return componentRef;
  }
}
