import {
  Component,
  InjectionToken,
  ViewChild,
  ViewContainerRef,
} from "@angular/core";

import { APP_ROOT_VIEW_CONTAINER_REF } from "./app.tokens";
import { ModalComponent } from "./modal/modal.component";
import { ModalService } from "./modal.service";

@Component({
  selector: "app-some-component",
  template: `
    <button (click)="openModal()">Open Modal</button>
    <!-- The modal container where dynamic components will be injected -->
  `,
  providers: [
    {
      provide: APP_ROOT_VIEW_CONTAINER_REF,
      useExisting: ViewContainerRef,
    },
  ],
})
export class AppComponent {
  // Get a reference to the template's view container

  constructor(private modalService: ModalService) {}

  openModal() {
    const modalRef = this.modalService.open(ModalComponent);

    modalRef.instance.close.subscribe(() => {
      this.modalService.close();
    });
  }
}
