// modal.service.ts

import {
  ApplicationRef,
  ComponentRef,
  Injectable,
  Injector,
  Type,
  ViewContainerRef,
} from "@angular/core";

@Injectable({
  providedIn: "root",
})
export class ModalService {
  private modalRefs: { [key: string]: ComponentRef<any> } = {};

  constructor(
    private injector: Injector,
    private viewContainerRef: ViewContainerRef
  ) {}

  open<T>(component: Type<T>, container: ViewContainerRef): ComponentRef<T> {
    this.close();

    const componentRef = container.createComponent(component, {
      injector: this.injector,
    });

    this.modalRefs[crypto.randomUUID()] = componentRef;

    return componentRef;
  }

  close() {
    if (this.modalRefs) {
      // this.modalRefs.destroy();
    }
  }
}
