// modal.service.ts

import {
  ApplicationRef,
  ComponentRef,
  Inject,
  Injectable,
  Injector,
  Type,
  ViewContainerRef,
  inject,
} from "@angular/core";

@Injectable({
  providedIn: "root",
})
export class ModalService {
  private modalRefs: { [key: string]: ComponentRef<any> } = {};
  private injector = inject(Injector);
  private viewContainerRef: ViewContainerRef | undefined = undefined;

  constructor() {}

  init(vcr: ViewContainerRef) {
    this.viewContainerRef = vcr;
  }

  open<T>(component: Type<T>): any {
    if (!this.viewContainerRef) {
      throw "ViewContainerRef is not initialized";
    }

    const refIdentifier = crypto.randomUUID();

    const componentRef = this.viewContainerRef?.createComponent(component, {
      injector: this.injector,
    });

    this.modalRefs[refIdentifier] = componentRef;

    return {
      close: () => {
        const ref = refIdentifier;
        this.close.bind(this)(ref);
      }
    };
  }

  close(refIdentifier: string) {
    this.modalRefs[refIdentifier].destroy();
  }
}
